
public class Arthemetic {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 100; 
		int b = 50;
		System.out.print("The Value of a+b is ");
    System.out.println(a+b);
    System.out.print("The Value of a-b is ");
    System.out.println(a-b);
    System.out.print("The Value of a/b is ");
    System.out.println(a/b);
    System.out.print("The Value of a*b is ");
    System.out.println(a*b);
    System.out.print("The Value of a%b is ");
    System.out.println(a%b);
	}

}
